interactions.menu:
(
  $interactions := $.interactions;
  $makeLocation := function($id) {(
      $arrleft := function($arr ,$count) {
          $map($arr, function($v, $i) {
          $i <= $count ? $v
          })
      };
      $domains := $split($id, ".");
      "Взаимодействия/" & $join($map($domains, function($domain, $index) {(
          $lookup($interactions, $join($arrleft($domains, $index), ".")).title
      )}), "/");
  )};
  $append([{
      "icon": *.icon,                                                 /* Получаем иконку */
      "link": "/entities/interactions/tree",                           /* Ссылка на форму представления tree (дерево дерево объектов "interactions") */
      "location": "Взаимодействия"                                    /* Расположение в меню */
  }
  ],
      [$.interactions.$spread().{
          "icon": *.icon,                                             /* Получаем иконку */
          "link": "/entities/interactions/blank?id=" & $keys()[0],     /* Формируем ссылку на бланк документ */
          "location": $makeLocation($keys()[0])                       /* Формируем расположение в меню */
      }][location]
  );
)

tree.source:
(
    $set("rpev-id", undefined);
    $arrleft := function($arr ,$count) {
        $map($arr, function($v, $i) {
            $i <= $count ? $v
        })
    };
    $interactions := $.interactions;
    [$interactions.$spread().($merge([{"id" : $keys()[0]}, $.*]))^(id).(
        $prev_nodes := $split($get("rpev-id"), ".");
        $prev_level := $count($prev_nodes);
        $curr_nodes := $split(id, ".");
        $set("isdiff", false);
        $result := $map($curr_nodes, function($v, $i) {(
            $set("isdiff", $get("isdiff") or $prev_level = 0 or $prev_level <= $i or $v != $prev_nodes[$i]) ? (
                $id := $join($arrleft($curr_nodes, $i), ".");
                $interaction :=  $lookup($interactions, $id);
                {
                    "id": $id,
                    "level": $pad("", $i + 2, "*"),
                    "title": ($interaction ? $interaction.title : $id),
                    "link": $id=$params.id ? undefined : "/entities/interactions/blank?id=" & $id
                }
            );
        )});
        $set("rpev-id", id);
        $result 
    )];
)

dochub.smartants.examples.simple-query.source:
(
  /* Определяем фильтр для компонентов архитектуры */
  $matcher := /^[a-zA-Z]*\.[a-zA-Z]*$/i;
  /* Генерируем структуру для диаграммы */
  {
    /* Формируем ноды из компонентов */
    "nodes": $merge(components.$spread().(
      /* Получаем идентификатор компонента */
      $id := $keys()[0];
      /* Если идентификатор компонента удовлетворяет фильтру, создаем ноду */
      $matcher($id) ? {
          /* Отсекаем домен верхнего уровня для исключения отображения компонентов в иерархии */
          $split($id, ".")[1]: {
              "title": *.title,
              /* Преобразуем entity компонентов в понятные диаграмме символы actor -> user */
              "symbol": *.entity = "actor" ? "user" : *.entity
          }
      }

    ))
  }
)

dochub.example.network.source:
(
  $manifest := $;
  /* Перебираем все компоненты */
  $adges  := $distinct(components.$spread().(
    $node_id := $keys()[0];
    $node := $.*;
    /* Определяем связи */
    $node.links.{
      "from": $node_id,
      "to": id,
      "label": title
    }
  ));
  /* Выявляем ноды из связей */
  $nodes := $distinct($append($adges.from, $adges.to)).(
    $struct := $split($, ".");
    $group := $replace($, "." & $reverse($struct)[0], "");
    {
      "id": $,
      "label": $lookup($manifest.components, $).title,
      "group": $group,
      "level": $count($struct),
      /* Позиционируем ноду тем выше, чем выше слой */
      "y": $count($struct) * 200
    }
  );
  /* Добавляем ноды легенды */
  $nodes := $append($nodes, (
    /* Получаем максимальную точку позиции нод */
    $height := $max($nodes.y);
    $groups := $distinct($nodes.group)^($);
    /* Вычисляем центр легенды */
    $offset := -($height / 2);
    /* Определяем шаг легенды */
    $step := $height / $count($groups);
    /* Строим легенду */
    $map($groups, function($v, $i, $a) {
      {
        "id": "legend-" & $string($i),
        "x": -600,
        "y": $i * $step + $offset,
        "shape": "square",
        "label": $v,
        "group": $v,
        "value": 1,
        "fixed": true,
        "physics": false
      }
    })
  ));
  /* Выводим результат */
  {
    "nodes": $nodes, /* Массив нод */
    "edges": $adges, /* Массив связей */
    "options": {     /* Параметры сетевой диаграммы https://visjs.github.io/vis-network/docs/network/ */
      "clickToUse": false /* Отключаем обязательный клик перед взаимодействем с пользователем */
    }
  }
)

